        }
        setErrorMessage('') //Đặt lại thông báo lỗi
    }

    return (
        <>
            <h1>Binary to Decimal Converter</h1>
            <StyledForm onSubmit={onFormSubmit}>
                <Field>
                    <Lable>Binary Input</Lable>
                    <div>
                        <BinaryTextInput />
